# match(switch) construct

a = 7                                                                                                                                                                   
b = a % 2                                                                                                                                                               
match b:                                                                                                                                                                
    case 1:                                                                                                                                                             
        print("Odd", )                                                                                                                                                  
        break                                                                                                                                                           
    case 0:                                                                                                                                                             
        print("Even", )                                                                                                                                                 
        break 
